        // _el(nodeName, [childs], [attrs])
        _el: function(str, childs, attributes)
        {
            var el = document.createElement(str),
                children = childs,
                attrs = attributes;

            if (children)
                domManipulation._childs(el, children)

            if (attrs && attrs.length)
                domManipulation._attr(el, attrs)

            return el;
        },
        // _t("some text")
