var nsGmx = nsGmx || {};

nsGmx.SubscriptionsWidget = (function() {
    function cycleGeoJson(gj) {
        if (gj.type === 'Feature') {
            gj = gj.geometry;
        }

        // clone deep
        gj = JSON.parse(JSON.stringify(gj));

        function ring(c) {
            c.push(c[0]);
            return c;
        }

        function rings(r) {
            return r.map(ring);
        }

        function multirings(r) {
            return r.map(rings);
        }

        switch (gj.type) {
            case 'Point':
                return gj;
            case 'LineString':
                return gj;
            case 'Polygon':
                return {
                    type: gj.type,
                    coordinates: rings(gj.coordinates)
                };
            case 'MultiPoint':
                return gj;
            case 'MultiPolygon':
                return {
                    type: gj.type,
                    coordinates: multirings(gj.coordinates)
                };
            case 'MultiLineString':
                return gj;
            case 'GeometryCollection':
                return {
                    type: gj.type,
                    geometries: gj.geometries.map(cycleGeoJson)
                };
            default:
                throw new Error('cycleGeoJson() requires a valid GeoJSON Feature or geometry object as input');
        }
    }

    // Sync для моделей подписок (см. Backbone.sync)
    // Для функции sync требуется провайдер и дерево слоёв,
    // по-этому мы не можем определить её сразу и используем фабрику,
    // чтобы получить её в дальнейшем.
    // То же касается моделей и коллекций подписок.
    var subscriptionSyncFactory = function(widgetOptions) {
        return function(method, model, syncOptions) {
            if (method === 'read') {
                var params = {};
                if (model.get('ID')) {
                    // fetching model
                    params.ID = model.get('ID');
                }
                widgetOptions.provider.sendGetRequest('Subscribe/Get.ashx', params).then(function(response) {
                    if (model.get('ID')) {
                        syncOptions.success(response.Result[0]);
                    } else {
                        var filteredSubscriptions = _(response.Result).filter(function(s) {
                            return widgetOptions.subscriptionsLayersTree.find(s.LayerName);
                        });
                        syncOptions.success(filteredSubscriptions);
                    }
                }).fail(function(response) {
                    syncOptions.error(response.ErrorInfo);
                });
            } else if (method === 'create') {
                widgetOptions.provider.sendPostRequest('Subscribe/Add.ashx', model.toJSON()).then(function(response) {
                    syncOptions.success(response.Result);
                }).fail(function(response) {
                    syncOptions.error(response.ErrorInfo);
                })
            } else if (method === 'update') {
                widgetOptions.provider.sendPostRequest('Subscribe/Update.ashx', model.toJSON()).then(function(response) {
                    syncOptions.success(response.Result);
                }).fail(function(response) {
                    syncOptions.error(response.ErrorInfo);
                })
            } else if (method === 'delete') {
                widgetOptions.provider.sendPostRequest('Subscribe/Delete.ashx', model.toJSON()).then(function(response) {
                    syncOptions.success();
                }).fail(function(response) {
                    syncOptions.error(response.ErrorInfo);
                })
            }
        };
    };

    // SubscriptionModel
    var subscriptionModelFactory = function(widgetOptions) {
        return Backbone.Model.extend({
            url: '',
            idAttribute: 'ID',
            sync: subscriptionSyncFactory(widgetOptions),
            defaults: {
                Title: 'Title'
            },
            validate: function(attrs, options) {
                var layerInTree = function(layerId) {
                    var inTree = false;
                    widgetOptions.subscriptionsLayersTree.eachNode(function(node) {
                        if (node.get('properties').LayerID === layerId) {
                            inTree = true;
                        }
                    }, true);
                    return inTree;
                };

                if (!_.isString(attrs.Title) || !attrs.Title) {
                    return 'Title';
                }

                if (!attrs.LayerName || !layerInTree(attrs.LayerName)) {
                    return 'LayerName';
                }

                if (!attrs.wkt || !wellknown.parse(attrs.wkt)) {
                    return 'Wkt';
                }

                var f = L.GeoJSON.geometryToLayer(
                    wellknown.parse(
                        this.get('wkt')
                    )
                );

                if (L.gmxUtil.getArea(f.toGeoJSON()) > widgetOptions.maxArea) {
                    return 'Area';
                }
            }
        });
    };

    // SubscriptionsCollection
    var subscriptionsCollectionFactory = function(widgetOptions) {
        return Backbone.Collection.extend({
            url: '',
            idAttribute: 'ID',
            model: subscriptionModelFactory(widgetOptions),
            sync: subscriptionSyncFactory(widgetOptions)
        });
    };

    var SubscriptionIdleView = Thorax.View.extend({
        template: Handlebars.compile(nsGmx.Templates.SubscriptionsWidget.subscription_idle)
    });

    // SubscriptionsEditView
    var subscriptionsEditViewFactory = function(widgetOptions) {
        return Thorax.View.extend({
            template: Handlebars.compile(nsGmx.Templates.SubscriptionsWidget.subscription_edit),
            initialize: function() {
                var self = this;
                this.error = '',
                    this.showRemoveButton = true;

                this.layersTreeWidget = new nsGmx.LayersTreeWidget({
                    layersTree: widgetOptions.subscriptionsLayersTree
                });

                this.layersTreeWidget.on('ready', function() {
                    this.trigger('resize');
                }.bind(this));

                this.layersTreeWidget.on('resize', function() {
                    this.trigger('resize');
                }.bind(this));

                this.drawingObjectsWidget = new nsGmx.DrawingObjectsListWidget({
                    drawingManager: widgetOptions.drawingManager,
                    showStyleButton: false,
                    showRemoveButton: false,
                    selectMode: 'single',
                    markGeometries: false
                });

                this.drawingObjectsWidget.on('ready', function() {
                    this.trigger('resize');
                }.bind(this));

                this.drawingObjectsWidget.on('resize', function() {
                    this.trigger('resize');
                }.bind(this));

                this.alertWidget = new nsGmx.AlertWidget();

                this.drawingObjectsWidget.on('objectToggle', function(feature, visible) {
                    if (visible) {
                        self.trigger('featureSelected', feature);
                    }
                });

                this.drawingObjectsWidget.on('objectFocus', function(feature) {
                    if (feature.options.type.toLowerCase() === 'point') {
                        widgetOptions.leafletMap.panTo(L.gmxUtil.geoJSONGetLatLng(feature.toGeoJSON()));
                    } else {
                        widgetOptions.leafletMap.fitBounds(feature.getBounds());
                    }
                });

                this.on('rendered', function () {
                    this.layersTreeWidget.appendTo(this.$el.find('.subscription-layersTreeWidget'));
                    this.drawingObjectsWidget.appendTo(this.$el.find('.subscription-drawingObjectsWidget'));
                }.bind(this));
            },
            events: {
                'ready': function() {
                    this.alertWidget.clear();
                },
                'click .subscription-debugButton': function() {
                    console.log(this.model.attributes)
                },
                'click .subscription-saveButton': function() {
                    var title = this.$el.find('.subscription-inputTitle').val();

                    var layerId;
                    widgetOptions.subscriptionsLayersTree.eachNode(function(node) {
                        if (node.get('visible')) {
                            layerId = node.id;
                        }
                    }, true);

                    var wkt = '';
                    var feature = this.drawingObjectsWidget.getSelectedObjects()[0];
                    if (feature) {
                        wkt = wellknown.stringify(
                            cycleGeoJson(feature.toGeoJSON())
                        );
                    }

                    this.trigger('saveButtonClick', this.model, title, layerId, wkt);
                },
                'click .subscription-removeButton': function() {
                    this.trigger('removeButtonClick', this.model);
                },
                'click .subscription-cancelButton': function() {
                    this.trigger('cancelButtonClick', this.model);
                }
            },
            context: function() {
                return {
                    error: this.error,
                    title: this.model.get('Title'),
                    layersTreeWidget: this.layersTreeWidget,
                    drawingObjectsWidget: this.drawingObjectsWidget,
                    showRemoveButton: this.showRemoveButton
                }
            },
            showError: function(errorMessage) {
                this.alertWidget.clear();
                this.alertWidget.push(nsGmx.Translations.getText('subscriptions.alert' + errorMessage), 'error');
            },
            // вызывать только после события ready
            hideRemoveButton: function() {
                this.showRemoveButton = false;
                this.render();
            }
        });
    };

    // SubscriptionAddView
    var subscriptionAddViewFactory = function(widgetOptions) {
        var SubscriptionModel = subscriptionModelFactory(widgetOptions);
        var SubscriptionEditView = subscriptionsEditViewFactory(widgetOptions);

        var SubscriptionAddButtonView = Thorax.View.extend({
            template: Handlebars.compile(nsGmx.Templates.SubscriptionsWidget.addButton),
        });

        return Thorax.LayoutView.extend({
            initialize: function() {
                var self = this;

                this.editView = new SubscriptionEditView({
                    model: new SubscriptionModel()
                });
                this.editView.on('cancelButtonClick', function() {
                    self.switchIdle();
                });
                this.editView.on('ready', function() {
                    self.editView.setModel(new SubscriptionModel());
                    // то, чем добавление отичается от редактирования
                    self.editView.hideRemoveButton();
                    self.editView.drawingObjectsWidget.clearSelection();
                });
                this.editView.retain();

                this.idleView = new SubscriptionAddButtonView();
                this.idleView.on('click', function() {
                    self.switchAdd();
                });
                this.idleView.retain();

                this.on('ready', function() {
                    self.switchIdle();
                });
            },
            switchAdd: function() {
                this.setView(this.editView);
                this.trigger('switchedAdd');
            },
            switchIdle: function() {
                this.setView(this.idleView);
                this.trigger('switchedIdle');
            }
        });
    };

    // SubscriptionView
    var subscriptionViewFactory = function(widgetOptions) {
        var SubscriptionEditView = subscriptionsEditViewFactory(widgetOptions);
        // Представление для подписки. Имеет два состояния - Edit и Idle
        return Thorax.LayoutView.extend({
            constructor: function(options) {
                // Thorax CollectionView присваивает модель
                // всем своим дочерним элементам. В нашем случае модель нужна
                // только в дочернем представлении SubscriptionView (EditView или
                // IdleView). Более того, если та же модель будет служить моделью
                // и для SubscriptionView, то каждом её изменении в
                // SubscriptionEditView эти изменения будут обрабатываться и в
                // SubscriptionView, что приведёт к рендерингу и потере дочернего
                // представления. По-этому мы переопределяем конструктор так, чтобы
                // модель использовалась только в дочерних представлениях.
                this.subscriptionModel = options.model;
                this.editView = new SubscriptionEditView({
                    model: options.model
                });
                this.editView.retain();
                this.idleView = new SubscriptionIdleView({
                    model: options.model
                });
                this.idleView.retain();
                Thorax.LayoutView.apply(this);
            },
            initialize: function() {
                var self = this;
                this.on('ready', function() {
                    self.switchIdle();
                }.bind(this));
            },
            switchEdit: function(model) {
                this.setView(this.editView);
            },
            switchIdle: function(model) {
                this.setView(this.idleView);
            },
            events: {
                'destroyed': function() {
                    this.editView.release();
                    this.idleView.release();
                }
            }
        });
    };

    // SubscriptionsCollectionView
    var subscriptionsCollectionViewFactory = function(widgetOptions) {
        var SubscriptionView = subscriptionViewFactory(widgetOptions);
        var SubscriptionEditView = subscriptionsEditViewFactory(widgetOptions);
        return Thorax.CollectionView.extend({
            itemView: SubscriptionView,
            initialize: function() {
                this.on('child', function(childView) {
                    var self = this;

                    childView.idleView.on('click', function() {
                        self.setActiveSubscription(childView.idleView.model);
                    });

                    childView.editView.on('saveButtonClick', function(subscriptionModel, title, layerId, wkt) {
                        subscriptionModel.set('Title', title);
                        subscriptionModel.set('LayerName', layerId);
                        subscriptionModel.set('wkt', wkt);

                        // В Thorax'е есть баг, который не позволяет производить
                        // валидацию модели, которая принадлежит коллекции, используемой
                        // в CollectionView. По-этому мы клонируем модель и выполняем
                        // валидацию на ней.
                        var m = subscriptionModel.clone();

                        if (!m.isValid()) {
                            childView.editView.showError(m.validationError);
                        } else {
                            subscriptionModel.save({}, {
                                success: function() {
                                    self.trigger('subscriptionSaved');
                                    self.releaseActiveSubscription();
                                },
                                error: function(model, err) {
                                    childView.editView.showError('ServerError');
                                }
                            });
                        }
                    });
                    childView.editView.on('removeButtonClick', function(subscriptionModel) {
                        self.trigger('subscriptionRemoved', subscriptionModel);
                        subscriptionModel.destroy();
                    });
                    childView.editView.on('cancelButtonClick', function(subscriptionModel) {
                        self.releaseActiveSubscription();
                    });
                    childView.editView.on('featureSelected', function(feature) {
                        self.trigger('featureSelected', feature);
                    });
                    childView.editView.on('resize', function() {
                        self.trigger('resize');
                    });
                    childView.on('change:view:end', function() {
                        self.trigger('resize');
                    });
                    this.on('rendered:item', function() {
                        self.trigger('resize');
                    });
                });
            },
            setActiveSubscription: function(newSubscriptionModel) {
                var self = this;
                _(this.children).forEach(function(subscriptionLayoutView) {
                    if (subscriptionLayoutView.subscriptionModel.get('ID') === newSubscriptionModel.get('ID')) {
                        // устанавляиваем подписку в режим редактирования
                        subscriptionLayoutView.switchEdit(subscriptionLayoutView.subscriptionModel);
                        // сохраняем модель для восстановления изменений (напр. по клике на 'cancel')
                        self.retainedModel = subscriptionLayoutView.subscriptionModel.clone();
                    } else if (
                        self.previousSubscriptionModel &&
                        (subscriptionLayoutView.subscriptionModel.get('ID') === self.previousSubscriptionModel.get('ID'))
                    ) {
                        // снимаем редактирование с предыдущей подписки
                        subscriptionLayoutView.switchIdle();
                        self.trigger('subscriptionReleased');
                    }
                });
                this.previousSubscriptionModel = newSubscriptionModel;

                this.trigger('subscriptionSelected', newSubscriptionModel);
            },
            releaseActiveSubscription: function() {
                var self = this;
                _(this.children).forEach(function(subscriptionLayoutView) {
                    if (
                        self.previousSubscriptionModel &&
                        (subscriptionLayoutView.subscriptionModel.get('ID') === self.previousSubscriptionModel.get('ID'))
                    ) {
                        // снимаем редактирование с текущей подписки
                        subscriptionLayoutView.switchIdle();
                        self.previousSubscriptionModel = null;
                    }
                });

                this.trigger('subscriptionReleased');
            }
        });
    };

    var GeometryManager = function(options) {
        this._drawingManager = options.drawingManager;
        this._leafletMap = options.leafletMap;
        this._invalidGeometryColor = options.invalidGeometryColor;
        this._nativeGeometryColor = options.nativeGeometryColor;
        this._maxArea = options.maxArea;
        this._activeGeometry = null; // текущая редактируемая геометрия
        this._subscriptionGeometry = null; // геометрия выбраной подписки
        this._onGeometryEdit = this._paintGeometry.bind(this);
    };

    GeometryManager.prototype._paintGeometry = function(e) {
        if (L.gmxUtil.getArea(e.target.toGeoJSON()) > this._maxArea) {
            e.target.setOptions({
                lineStyle: {
                    color: this._invalidGeometryColor
                },
                pointStyle: {
                    color: this._invalidGeometryColor
                }
            });
        } else {
            e.target.setOptions({
                lineStyle: {
                    color: this._nativeGeometryColor
                },
                pointStyle: {
                    color: this._nativeGeometryColor
                }
            });
        }
    };

    GeometryManager.prototype.setActiveGeometry = function(feature, focus) {
        var self = this;
        this.releaseActiveGeometry();

        this._activeGeometry = feature;

        this._activeGeometry.on('edit', this._onGeometryEdit);
        this._paintGeometry({
            target: feature
        });

        if (focus && self._leafletMap && self._activeGeometry) {
            self._leafletMap.fitBounds(self._activeGeometry.getBounds(), {
                animate: false
            });
        }
    };

    GeometryManager.prototype.getActiveGeometry = function() {
        return this._activeGeometry;
    };

    GeometryManager.prototype.releaseActiveGeometry = function() {
        if (this._activeGeometry) {
            this._activeGeometry.off('edit', this._onGeometryEdit);
            if (this._nativeGeometryColor) {
                this._activeGeometry.setOptions({
                    lineStyle: {
                        color: this._nativeGeometryColor
                    },
                    pointStyle: {
                        color: this._nativeGeometryColor
                    }
                });
            }
        }
    };

    GeometryManager.prototype.setSubscriptionGeometry = function(feature) {
        this._subscriptionGeometry = feature;
    };

    GeometryManager.prototype.removeSubscriptionGeometry = function() {
        this._subscriptionGeometry && this._subscriptionGeometry.remove();
    };

    // widget view
    return Thorax.View.extend({
        className: 'subscriptionsWidget ui-widget',
        template: Handlebars.compile('{{view subscriptionAddView}}{{view collectionView}}'),
        initialize: function(options) {
            var self = this;

            var _options = this._options = {
                provider: options.subscriptionsResourceServer,
                subscriptionsLayersTree: options.subscriptionsLayersTree,
                drawingManager: options.drawingManager,
                leafletMap: options.leafletMap,
                maxArea: options.maxArea,
                debug: options.debug,
                invalidGeometryColor: options.invalidGeometryColor || '#AA0000'
            }

            var geometryManger = new GeometryManager({
                drawingManager: _options.drawingManager,
                invalidGeometryColor: _options.invalidGeometryColor,
                nativeGeometryColor: '#03f',
                maxArea: _options.maxArea,
                leafletMap: _options.leafletMap
            });

            var onGeometryAdd = function(drawingOptions) {
                geometryManger.setActiveGeometry(drawingOptions.object);
            };

            var SubscriptionsCollection = subscriptionsCollectionFactory(_options);

            var subscriptionsCollection = new SubscriptionsCollection();

            // передаём флаг reset, чтобы коллекция не отрендерилась дважды
            subscriptionsCollection.fetch({
                reset: true
            });

            var SubscriptionAddView = subscriptionAddViewFactory(_options);
            var SubscriptionsCollectionView = subscriptionsCollectionViewFactory(_options);

            this.collectionView = new SubscriptionsCollectionView({
                collection: subscriptionsCollection
            });

            this.collectionView.on('subscriptionSelected', function(subscriptionModel) {
                self.subscriptionAddView.switchIdle();

                var subscriptionGeometry = _options.drawingManager.add(
                    L.GeoJSON.geometryToLayer(
                        wellknown.parse(
                            subscriptionModel.get('wkt')
                        )
                    )
                );

                geometryManger.setActiveGeometry(subscriptionGeometry, true);
                geometryManger.setSubscriptionGeometry(subscriptionGeometry);

                _options.subscriptionsLayersTree.eachNode(function(node) {
                    node.set('visible', false);
                }, true);

                _options.subscriptionsLayersTree
                    .find(subscriptionModel.get('LayerName'))
                    .set('visible', true);

                _options.drawingManager.on('add', onGeometryAdd);
            });

            this.collectionView.on('subscriptionReleased', function() {
                geometryManger.releaseActiveGeometry();
                geometryManger.removeSubscriptionGeometry();
                _options.drawingManager.off('add', onGeometryAdd);
            });

            this.collectionView.on('subscriptionRemoved', function() {
                geometryManger.releaseActiveGeometry();
                geometryManger.removeSubscriptionGeometry();
                _options.drawingManager.off('add', onGeometryAdd);
            });

            this.collectionView.on('subscriptionSaved', function(model) {
                geometryManger.setSubscriptionGeometry(geometryManger.getActiveGeometry());
            });

            this.collectionView.on('featureSelected', function(feature) {
                geometryManger.setActiveGeometry(feature, true);
            });

            this.collectionView.on('resize', function() {
                self.trigger('resize');
            });

            this.subscriptionAddView = new SubscriptionAddView();

            this.subscriptionAddView.on('switchedAdd', function() {
                self.collectionView.releaseActiveSubscription();
                _options.drawingManager.on('add', onGeometryAdd);
            });

            this.subscriptionAddView.on('switchedIdle', function() {
                geometryManger.releaseActiveGeometry();
                geometryManger.removeSubscriptionGeometry();
                _options.drawingManager.off('add', onGeometryAdd);
            });

            this.subscriptionAddView.on('change:view:end', function() {
                self.trigger('resize');
            });

            this.subscriptionAddView.editView.on('saveButtonClick', function(model, title, layerId, wkt) {
                model.set('Title', title);
                model.set('LayerName', layerId);
                model.set('wkt', wkt);

                // Баг Thorax'а. См. выше
                var m = model.clone();

                if (!m.isValid()) {
                    self.subscriptionAddView.editView.showError(m.validationError);
                } else {
                    subscriptionsCollection.create(model, {
                        wait: true,
                        success: function() {
                            geometryManger.setSubscriptionGeometry(geometryManger.getActiveGeometry());
                            self.subscriptionAddView.switchIdle();
                        },
                        error: function(model, err) {
                            self.subscriptionAddView.editView.showError(err.ErrorMessage ? err.ErrorMessage : 'server error');
                        }
                    });
                }
            });

            this.subscriptionAddView.editView.on('featureSelected', function(feature) {
                geometryManger.setActiveGeometry(feature, true);
            });
        }
    });
})();
;
