nsGmx.FilteringListWidget = nsGmx.CompositeScrollView.extend({
    options: {
        filterWidgetOptions: {
            field: 'class'
        },
        listWidgetOptions: {
            itemView: nsGmx.MarkerItemView,
            reEmitEvents: []
        },
        collection: null
    },

    // <Object> options.filterWidgetOptions
    // <Object> options.listWidgetOptions
    initialize: function(opts) {
        var options = $.extend(true, {}, this.options, opts);

        this.filterWidget = new nsGmx.CollectionFilterWidget(options.filterWidgetOptions);
        this.listWidget = new nsGmx.SwitchingCollectionWidget(options.listWidgetOptions);
        this.setCollection(options.collection);

        // re-emit events from listWidget
        options.listWidgetOptions.reEmitEvents.map(function (evName) {
            this.listWidget.on(evName, function () {
                var argsArr = Array.prototype.slice.call(arguments);
                this.trigger.apply(this, [evName].concat(argsArr));
            }.bind(this));
        }.bind(this));

        this._activeValues = this.filterWidget.getActiveValues();
        this.filterWidget.on('change', function(activeValues) {
            this._activeValues = activeValues;
            this._updateListWidget();
        }.bind(this));

        nsGmx.CompositeScrollView.prototype.initialize.call(this, {
            staticWidget: this.filterWidget,
            scrollingWidget: this.listWidget,
            customScrollbar: options.customScrollbar
        });

        this.options = _.extend(this.options, options);
    },

    setCollection: function(collection) {
        if (!collection) {
            if (!this.collection) {
                this.collection = new Backbone.Collection();
            }
            return;
        }
        this.collection && this.collection.off('update', this._onCollectionUpdate, this);
        this.collection = collection;
        this.collection.on('update', this._onCollectionUpdate, this);
        this._onCollectionUpdate();
    },

    _onCollectionUpdate: function () {
        this._updateFilterWidget();
        this._activeValues = this.filterWidget.getActiveValues();
        this._updateListWidget();
        this.reset();
    },

    _updateFilterWidget: function () {
        this.filterWidget.setCollection(this.collection);
    },

    _updateListWidget: function () {
        this.listWidget.setCollection(this._getFilteredCollection(this.collection));
    },

    _getFilteredCollection: function() {
        var collection = this.collection;
        if (!collection) {
            return new Backbone.Collection();
        }
        return new Backbone.Collection(collection.filter(function(model) {
            return this._activeValues.indexOf(model.get(this.options.filterWidgetOptions.field)) > -1;
        }.bind(this)));
    }
});

